import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';


const root = document.getElementById('root');
// let name = "Earthrelmer";

ReactDOM.render(
  <div style={container}>
  <h1 style={heading}>Hello {name} <br /> Greetings</h1>
  <input style={input} type="text" />
  <button id="button" style={button}>Display</button>
  </div>,
  root
);

const but = document.getElementById('button');
  let date = new Date();
  let dateTime = date.getHours();
  let greet;

function DateAndGreet () {
  if (dateTime > 21) {
    greet = "Good Night"
  } else if (dateTime > 17) {
    greet = "Good Evening"
  } else if (dateTime > 12) {
    greet = "Good Afternoon"
  } else {
    greet = "Good Morning"
  }
}

but.addEventListener('click', item => {
  const select = item.toElement.parentElement.childNodes;
  DateAndGreet ()
  name = select[1].value;
  select[0].innerHTML = `Hello ${name} <br> ${greet}`;
});
