import React from 'react';


function video () {
    return (
        <video className="video" autoPlay loop>
            <source src="video.mp4"></source>
        </video>
    )
}

function mBox (val) {
    return (
        <Box value={val}></Box>
    )
}

function Grid () {
    return (
        <>
            <div className="grid">
                {Data.map(mBox)}
            </div>

            
        </>
    )
}


function Box (props) {
    return (
        <div className="box boxing">
            <div className="element fade">
                <p>{props.value}</p>
            </div>
        </div>
    )
}


let numbered = [];
let bombs = [];
let blank = [];
for (let i = 0; i < 75; i++) {
    numbered[i] = Math.floor(Math.random() * 4);
}
for (let i = 0; i < 10; i++) {
    bombs[i] = 'p';
}
for (let i = 0; i < 15; i++) {
    blank[i] = "5";
    
}

let bombBlank = blank.concat(bombs);
const shuffle = numbered.concat(bombBlank);

// Random Array Algorithm
const shuffleData = array => {
    for (let i = array.length; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        const temp = array[i];
        array[j] = temp;
    }
    return array;
}
let Data = shuffleData(shuffle);

export default video;
export {Grid};
