import React from 'react';

let name = "Earthrelmer";


const container = {
    padding: '50px 0',
    width: '700px',
    background: "#ff5222",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    marginTop: "120px",
    flexDirection: "column",
    gap: "20px"
}
const heading = {
    fontSize: "2.2em",
    fontFamily: "'impact'",
    textTransform: "upperCase",
    textAlign: "center",
    color: "white",
    textShadow: "1px 3px black",
}
const input = {
    fontSize: "20px",
    height: "30px",
    width: "250px"
}
const button = {
    padding: "5px 10px",
    border: "none",
    fontSize: "16px"
}


function App() {
    return (
        <div style={container}>
            <h1 style={heading}>Hello {name} <br /> Greetings</h1>
            <input style={input} type="text" />
            <button id="button" style={button}>Display</button>
        </div>
    )
}


const but = document.getElementById('button');
let date = new Date();
let dateTime = date.getHours();
let greet;

function DateAndGreet() {
    if (dateTime > 21) {
        greet = "Good Night"
    } else if (dateTime > 17) {
        greet = "Good Evening"
    } else if (dateTime > 12) {
        greet = "Good Afternoon"
    } else {
        greet = "Good Morning"
    }
}

but.addEventListener('click', item => {
    const select = item.toElement.parentElement.childNodes;
    DateAndGreet()
    name = select[1].value;
    select[0].innerHTML = `Hello ${name} <br> ${greet}`;
});

export default App;
