import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

const container = {
  padding : '50px 120px',
  background : "#fff999",
  display : "flex",
  justifyContent : "center",
  alignItems : "center",
  marginTop : "120px",
  flexDirection : "column",
  gap : "20px"
}
const heading = {
  fontSize : "2.2em",
  fontFamily : "'impact'",
  textTransform : "upperCase"
}
const input = {
  fontSize : "20px",
  height : "30px",
  width : "250px"
}
const button = {
  padding : "5px 10px",
  border : "none",
  fontSize : "16px"
}

const root = document.getElementById('root');
// let name = "Earthrelmer";

ReactDOM.render(
  <div style={container}>
  <h1 style={heading}>Helo {name}</h1>
  <input style={input} type="text" />
  <button id="button" style={button}>Display</button>
  </div>,
  root
);

let name = "flana";
const but = document.getElementById('button');
but.addEventListener('click', item => {
  const select = item.toElement.parentElement.childNodes;
  console.log(select[1].value);
  name = select[1].value;
  console.log(name);

  select[1].innerHTML = `Helo {name}`;
});
