import React from 'react';


function video () {
    return (
        <video className="video" autoPlay loop>
            <source src="video.mp4"></source>
        </video>
    )
}

function mBox (val) {
    <Box value={val}></Box>
     
}

function Grid () {
    return (
        <>
            <div className="grid">
                <Box value={Data[0]}></Box>
            </div>

            Data.map(mBox);
        </>
    )
}


function Box (props) {
    return (
        <div className="box">
            <h1>{props.value}</h1>
        </div>
    )
}


let numbered = [];
let bombs = [];
let blank = [];
for (let i = 0; i < 75; i++) {
    numbered[i] = Math.floor(Math.random() * 4);
}
for (let i = 0; i < 10; i++) {
    bombs[i] = 'pics.png';
}
for (let i = 0; i < 15; i++) {
    blank[i] = " ";
    
}
const shuffle = numbered.concat(bombs, blank);

// Random Array Algorithm
const shuffleData = array => {
    for (let i = array.length; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        const temp = array[i];
        array[j] = temp;
    }
    return array;
}
let Data = shuffleData(shuffle);


export default video;
export {Grid};
